sys_push_notif_msg_content.script:
(function buildJSON( /*GlideRecord*/ current, /*String*/ message, /*Object*/ attributes) {
        var expenseGr = new GlideRecord('x_avnet_04_reyno_0_expense_request');
        if (expenseGr.get(current.task.toString())) {
			var days = Math.floor(new GlideDateTime(current.time_left).getNumericValue()/(1000 * 3600 * 24));
            var layoutFields = {
                "Identifier": "number",
                "Description": "short_description",
                "Status": "state"
            };
            var json = '';
            if (expenseGr.state == -6) {
                json = new global.ActionablePushPayloadBuilder(expenseGr, "1fce2d804643cd90ea4c470f580d55e1", layoutFields).buildJSON();
            } else {
                json = new global.ActionablePushPayloadBuilder(expenseGr, "93ce2d804443cd90c9c7745e2e8012e2", layoutFields).buildJSON();
            }
            json.Layout.Status = "Due in " + days + " days";
            return json;
		}

        })(current, message, attributes);
