sys_ux_data_broker_transform.script:
function transform() {
    var upcomingBudget = false;
    var data = {};
    var budgetRec = new GlideRecord("x_avnet_04_reyno_0_budget_definition");
    budgetRec.addQuery("state", 20);
    budgetRec.orderBy('start_date');
    budgetRec.setLimit(1);
    budgetRec.query();
    if (!budgetRec.next()) {
        upcomingBudget = true;
		data.budgetAvailable = upcomingBudget;
    }
    var gr = new GlideRecord('x_avnet_04_reyno_0_budget_definition');
    gr.addEncodedQuery('state=10');
    gr.query();
    if (gr.next()) {
        data.title = gr.name.toString();
        data.reqTitle = gs.getMessage('Requests: ' + gr.name);
        data.budgetTitle = gs.getMessage('Budget: ' + gr.name);
    }
    return data;
}
